mapper.select:
select 
t2.name username, t1.id, t1.name, t1.link, t1.createuserid, t1.createtime, t1.orderby, t1.bankuaiid, t1.img,t1.isshow, t1.type
from jbbs_link t1
left join jbbs_user t2 on t1.createuserid=t2.id
where  1=1

    and t1.id = #{id,jdbcType=INTEGER}


    and t1.createuserid = #{createuserid,jdbcType=INTEGER}


    and t1.bankuaiid = #{bankuaiid,jdbcType=INTEGER}


    and t1.isshow = #{isshow,jdbcType=VARCHAR}

order by t1.orderby

SELECT t2.name username, t1.id, t1.name, t1.link, t1.createuserid, t1.createtime, t1.orderby, t1.bankuaiid, t1.img, t1.isshow, t1.type, t3.name bankuainame FROM jbbs_link t1 left join jbbs_user t2 on t1.createuserid=t2.id left join jbbs_bankuai t3 on t1.bankuaiid=t3.id where 1=1
and t1.id = #{bean.id,jdbcType=INTEGER}
and t1.createuserid = #{bean.createuserid,jdbcType=INTEGER}
ORDER BY t1.isshow DESC ,t1.orderby DESC LIMIT ${pageNo}, ${pageSize}

select count(*) from jbbs_link t1 left join jbbs_user t2 on t1.createuserid=t2.id where 1=1
and t1.id = #{bean.id,jdbcType=INTEGER}
and t1.createuserid = #{bean.createuserid,jdbcType=INTEGER}
