mapper.select:
SELECT 
t1.id, t1.startip, t1.endip, t1.type, t1.createtime, t1.createuserid, t1.isuse, t1.remarks,t2.loginname
FROM jbbs_stop_ip t1 LEFT JOIN jbbs_user t2 ON t1.createuserid=t2.id 
where 1=1

 and  t1.id = #{id,jdbcType=INTEGER}


 and  t1.isuse = #{isuse,jdbcType=VARCHAR}

SELECT t1.id, t1.startip, t1.endip, t1.type, t1.createtime, t1.createuserid, t1.isuse, t1.remarks,t2.loginname FROM jbbs_stop_ip t1 LEFT JOIN jbbs_user t2 ON t1.createuserid=t2.id where 1=1
and t1.id = #{bean.id,jdbcType=INTEGER}
ORDER BY t1.id desc LIMIT ${pageNo}, ${pageSize}
