mapper.select:
SELECT t1.id, t1.NAME, t1.createtime, t1.createuserid, t1.bankuai_id bankuaiid, t1.zhutiid, t1.lastupdate, t1.jinghua, t1.zhiding, t1.findcount, t1.huifuid, t2.name bankuainame, t3.name zhutiname, t2.yuming FROM jbbs_tiezi t1 left join jbbs_bankuai t2 on t1.bankuai_id=t2.id left join jbbs_zhuti t3 on t1.zhutiid=t3.id where 1=1
and t1.id = #{id,jdbcType=INTEGER}
and t1.NAME = #{name,jdbcType=VARCHAR}
and t1.createuserid = #{createuserid,jdbcType=INTEGER}
and t1.createtime = #{createtime,jdbcType=INTEGER}
and t1.bankuai_id = #{bankuaiId,jdbcType=INTEGER}
and t1.createuserid = #{createuserid,jdbcType=INTEGER}
and t1.isuse = #{isuse,jdbcType=VARCHAR}

    select  count(*) 
    from 
    	jbbs_tiezi t1 

    where 1=1

	   and t1.bankuai_id = #{bean.bankuaiId,jdbcType=INTEGER}


	   and t1.name like '%${bean.name}%'


	   and t1.zhutiid = #{bean.zhutiid,jdbcType=INTEGER}


	   and t1.createuserid = #{bean.createuserid,jdbcType=INTEGER}


        and t1.createtime >= '${bean.starttime} 00:00:00'


          and t1.createtime <= '${bean.endtime} 23:59:59' 


	   and t1.isuse = #{bean.isuse,jdbcType=VARCHAR}

SELECT t5.createtime huifucreatetime, t6.id huifuuserid, t6.loginname huifuusername, t7.id tieziuserid, t7.loginname tieziusername, t7.img tieziuserimg, t1.id, t1.name, t1.createtime, t1.createuserid, t1.bankuai_id bankuaiId, t1.jinghua, t1.zhiding, t1.findcount, t1.huifuid , t1.isuse , t2.name zhutiname , t3.name bankuainame , t3.yuming yuming , (SELECT COUNT(1) FROM jbbs_huifu WHERE tieziid = t1.id ) huifucount FROM jbbs_tiezi t1 LEFT JOIN jbbs_zhuti t2 ON t1.zhutiid = t2.id LEFT JOIN jbbs_bankuai t3 ON t3.id=t1.bankuai_id LEFT JOIN jbbs_huifu t5 ON t5.id = t1.huifuid LEFT JOIN jbbs_user t6 ON t5.createuserid = t6.id LEFT JOIN jbbs_user t7 ON t1.createuserid = t7.id where 1=1
and t1.bankuai_id = #{bean.bankuaiId,jdbcType=INTEGER}
and t1.name like '%${bean.name}%'
and t1.zhutiid = #{bean.zhutiid,jdbcType=INTEGER}
and t1.createuserid = #{bean.createuserid,jdbcType=INTEGER}
and t1.createtime >= '${bean.starttime} 00:00:00'
and t1.createtime <= '${bean.endtime} 23:59:59'
and t1.isuse = #{bean.isuse,jdbcType=VARCHAR}
ORDER BY ${bean.orderby} desc
ORDER BY t1.id desc
LIMIT ${pageNo}, ${pageSize}
